'use strict'

import 'babelify/polyfill';
import React from 'react';
import TransitionManager from 'react-transition-manager';
// TODO: see if there's a better way to get fonts in
import 'localfont';
import 'TweenMax';
import 'TimelineLite';
import 'TweenLite-ScrollToPlugin';
import 'TweenLite-EasePack';
import Tracking from '_lib/tracking';

import Router from 'flux/router';
import Store from 'flux/store';
import Nulls from 'flux/nulls';
import Navigation from 'modules/navigation';
import Footer from 'modules/footer';
import Modal from 'modules/modal';
import EntranceAnimation from 'elements/entrance-animation';
import ContactTray from 'components/contact-tray';
import TakeOver from 'modules/take-over';

const pageMap = {
  home: require('templates/page-home'),
  work: require('templates/page-work')
};

export default class App extends React.Component {
  constructor(props) {
    super(props);
    Store.loadData([{
        namespace: 'ustwo/v1/',
        type: 'studios'
      }]);
    this.state = Store.getData();
  }
  onStoreChange() {
    this.setState(Store.getData());
  }
  componentWillMount() {
    Router.init(this.props.initialUrl);
    window.Tracking = new Tracking();
  }
  componentDidMount() {
    Store.addChangeListener(this.onStoreChange);
  }
  componentWillUnmount() {
    Store.removeChangeListener(this.onStoreChange);
    window.Tracking = null;
  }
  onStoreChange = () => {
    this.setState(Store.getData());
  }
  render() {
    const state = this.state;
    const animationOptions = {
      ease: Power2.easeOut,
      y: -68,
      clearProps: 'transform'
    };
    let content;
    const headerDelay = 0.5 + (state.takeover !== Nulls.takeover ? 2 : 0);
    if(state.page === 'notfound') {
      content = <div className="app">404</div>;
    } else {
      content = (
        <div className="app">
          <EntranceAnimation className="nav-wrapper" delay={headerDelay} duration={0.5} options={animationOptions} findElement={element => element.children[0]}>
            <Navigation pages={state.gulpdata.pages} customClass="transparent-white" takeover={this.state.takeover !== Nulls.takeover} />
          </EntranceAnimation>
          <TransitionManager component="div" className="app__stage" duration="500">
            <div className="app__stage__page-container" key={this.state.page}>
              {this.getPage(this.state.page)}
              <Footer data={renderData['current_ustwo/v1/_studios']} />
            </div>
          </TransitionManager>
          <TransitionManager component="div" className="app__modal" duration="500">
            {this.renderModal()}
          </TransitionManager>
        </div>
      );
    }
    return content;
  }
  renderModal() {
    const takeover = this.state.takeover;
    const modalType = this.state.modal;
    let modal;
    if(this.state.takeover !== Nulls.takeover || modalType) {
      let content;
      let showBelowHeader = false;
      if(this.state.takeover !== Nulls.takeover) {
        showBelowHeader = true;
        content = <TakeOver takeover={takeover} />;
      } else {
        switch(modalType) {
          case 'contacts':
            content = <ContactTray />;
          break;
        }
      }
      modal = <Modal key={this.state.modal} belowHeader={showBelowHeader}>{content}</Modal>;
    }
    return modal;
  }
  getPage(pageId) {
    return React.createElement(pageMap[pageId], this.state);
  }
};
