import React from 'react';

export default class ContactDetail extends React.Component {
  render() {
    const detail = this.props.detail;
    return (
      <section className="contact-detail">
        <h1 className="contact-detail__title">{detail.title}</h1>
        <p className="contact-detail__description">{detail.description}</p>
        {detail.links.map(this.renderLink)}
      </section>
    );
  }
  renderLink(link) {
    let prefix = "";
    switch(link.type) {
      case 'email':
        prefix = "mailto:";
      break;
      case 'tel':
        prefix = "tel:";
      break;
    }
    return <a className={`contact-detail__link ${link.type}`} href={`${prefix}${link.url}`}>{link.text}</a>;
  }
};
