import React from 'react';

export default class SingleColumn extends React.Component {
  render() {
    return (
      <section className="single-column" style={{ backgroundColor: this.props.backgroundColor }}>
        <div className="single-column__content">
          <h2 className="single-column__content__heading" style={{ color: this.props.headingColour }}>{this.props.title}</h2>
          <hr className="single-column__content__rule" style={{ borderColor: this.props.ruleColour }} />
          {this.props.children}
        </div>
      </section>
    );
  }
}
