'use strict';

import React from 'react';
import classnames from 'classnames';

export default class StudioContact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false
    };
  }
  onClickTitle = () => {
    this.setState({
      open: !this.state.open
    });
  }
  renderCity() {
    const studio = this.props.studio;
    let city;
    switch(studio.name) {
      case 'London':
        city = (
          <p>
            <span className="locality">{studio.name}</span> <span className="postal-code">{studio.postcode}</span>
          </p>
        );
      break;
      case 'newyork':
        city = (
          <p>
            <span className="locality">{studio.name}</span>, <span className="region">{studio.region}</span> <span className="postal-code">{studio.postcode}</span>
          </p>
        );
      break;
      case 'Malm√∂':
        city = (
          <p>
            <span className="postal-code">{studio.postcode}</span> <span className="locality">{studio.name}</span>
          </p>
        );
      break;
      case 'Sydney':
        city = (
          <p>
            <span className="locality">{studio.name}</span> <span className="region">{studio.region}</span> <span className="postal-code">{studio.postcode}</span>
          </p>
        );
      break;
    }
    return city;
  }
  render() {
    const studio = this.props.studio;
    console.log('StudioContact render', studio);
    return (
      <li className={classnames('studio', studio.name, {
          open: this.state.open
        })}>
        <h1 className="studio-title" onClick={this.onClickTitle}>{studio.name}</h1>
        <div className="studio-details">
          <div className="vcard">
            <div className="adr">
              <p className="street-address">{studio.address[0]}</p>
              <p className="place">{studio.address[1]}</p>
              <p className="country-name">{studio.address[2]}</p>
            </div>
            <a className="url map" href={studio.map}>Google Maps</a>
          </div>
        </div>
      </li>
    );
  }
};
