'use strict';

import React from 'react';
import classnames from 'classnames';

export default class StudioContact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false
    };
  }
  onClickTitle = () => {
    this.setState({
      open: !this.state.open
    });
  }
  render() {
    const studio = this.props.studio;
    return (
      <li className={classnames('studio', studio.id, {
          open: this.state.open
        })}>
        <h1 className="studio-title" onClick={this.onClickTitle}>{studio.city}</h1>
        <div className="studio-details">
          <div className="vcard">
            <div className="adr">
              <p className="street-address">{studio.street}</p>
              <p><span className="locality">{studio.city}</span> {studio.region ? <span className="region">{studio.region}</span> : ''} <span className="postal-code">{studio.postcode}</span></p>
              <p className="country-name">{studio.country}</p>
            </div>
            <a className="url map" href={studio.map}>Google Maps</a>
          </div>
        </div>
      </li>
    );
  }
};
