'use strict';

import React from 'react';
import classnames from 'classnames';

import Router from 'flux/router';

export default class WorkItem extends React.Component {
  render() {
    const data = this.props.data;
    return (
      <li className={classnames(this.props.className, 'work-item')} style={{backgroundColor: data.bgColour}}>
        <div className="work-item__image-wrapper" style={{backgroundImage: `url('${data.image}')`}}>
          <img className="work-item__image-wrapper__image" src={data.image} />
        </div>
        <div className="work-item__details">
          <p className="work-item__details__type" style={{borderColor: data.secondaryColour}}>{data.type}</p>
          <h3 className="work-item__details__title" style={{color: data.primaryColour}}>{data.title}</h3>
          <p className="work-item__details__desc">{data.description}</p>
          <a className="work-item__details__cta" href={data.link} style={{borderColor: data.secondaryColour}} onClick={Router.override(data.link)}>Read more</a>
        </div>
      </li>
    );
  }
};
