'use strict';

import React from 'react';
import classnames from 'classnames';

import Router from 'flux/router';

export default class NavigationLink extends React.Component {
  render() {
    const classes = classnames("nav__menu__item", {
      selected: this.props.selected
    });
    return (
      <li className={classes}>
        <a href={this.props.url} className={`nav__menu__item__link u-bar-after--${this.props.colour}`} onClick={this.onClick}>{this.props.children}</a>
      </li>
    );
  }
  onClick = (e) => {
    e.preventDefault();
    this.props.onClick();
    Router.navigate(this.props.url);
  }
};
