'use strict';

import React from 'react';

import Router from 'flux/router';

export default class NavigationOverlayLink extends React.Component {
  render() {
    return (
      <li className="h3 nav__overlay__menu__item">
          <a href={this.props.url} className="" onClick={this.onClick}>{this.props.children}</a>
      </li>
    );
  }
  onClick = (e) => {
    this.props.onClick();
    Router.override(this.props.url);
  }
}
