import Store from 'flux/store';

const Actions = {
  goTo(pageId) {
    Store.setPage(pageId);
    Store.emitChange();
  },
  showContacts() {
    Store.showContacts();
    Store.emitChange();
  },
  closeTakeover() {
    Store.closeTakeover();
    Store.emitChange();
  },
  closeModal() {
    Store.closeModal();
    Store.emitChange();
  }
};

window.Actions = Actions;

export default Actions;
