import Store from 'flux/store';

const Actions = {
  goTo(pageId, itemsToLoad) {
    itemsToLoad && Store.loadData(itemsToLoad);
    Store.setPage(pageId);
    Store.emitChange();
  },
  showContacts() {
    Store.showContacts();
    Store.emitChange();
  },
  openNav() {
    Store.openNav();
    Store.emitChange();
  },
  closeNav() {
    Store.closeNav();
    Store.emitChange();
  },
  closeTakeover() {
    Store.closeTakeover();
    Store.emitChange();
  },
  closeModal() {
    Store.closeModal();
    Store.emitChange();
  }
};

window.Actions = Actions;

export default Actions;
