import Actions from 'flux/actions';

const handlers = {
  home() {
    Actions.goTo('home');
  },
  whatWeDo() {
    Actions.goTo('what-we-do', [{
      namespace: 'wp/v2/',
      type: 'pages',
      id: '66?_embed'
    }]);
  },
  caseStudy(cid) {
    Actions.goTo('case-study', [{
      namespace: 'ustwo/v1/',
      type: 'case-studies',
      id: cid
    }]);
  }
};

/**
  Example route definitions
  {
    '/': function () {},
    '/projects': function () {},
    '/projects/:pid': function (pid) {}
  }
*/
const routes = {
  home: {
    pattern: '/',
    handler: handlers.home
  },
  work: {
    pattern: '/what-we-do',
    handler: handlers.whatWeDo
  },
  caseStudy: {
    pattern: '/what-we-do/:cid',
    handler: handlers.caseStudy
  }
};

export default routes;
