import Actions from 'flux/actions';

const handlers = {
  home() {
    Actions.goTo('home');
  },
  work() {
    Actions.goTo('work');
  },
  caseStudy(cid) {
    Actions.goTo('case-study', [{
      namespace: 'ustwo/v1/',
      type: 'case-studies',
      id: cid
    }]);
  }
};

/**
  Example route definitions
  {
    '/': function () {},
    '/projects': function () {},
    '/projects/:pid': function (pid) {}
  }
*/
const routes = {
  home: {
    pattern: '/',
    handler: handlers.home
  },
  work: {
    pattern: '/work',
    handler: handlers.work
  },
  caseStudy: {
    pattern: '/work/:cid',
    handler: handlers.caseStudy
  }
};

export default routes;
